import React from "react";
import {
  CartTableContainer,
  ProductImage,
  BtnActions,
  Row,
  ButtonLink,  
  TableRow,
  Column,
  Image,
  Name,
  TableFooter,
  Button,
  ProductActionsChange,
  Quantity,
  TextContent,
  UnitPrice,
  TotalPrice
} from "./cart-table.styles";
import { getCartItems } from "../../../utils/connectDB";
import {createStructuredSelector} from "reselect";
import {selectCartItems} from "../../../redux/cart/cart.selectors";
import {connect} from "react-redux";
const CartTable = ({ mobileView, tabletView }) => {
  const cartItems = getCartItems();

  return (
    <CartTableContainer mobileView={mobileView} tabletView={tabletView}>
      {cartItems.map((cart) => (
        <TableRow key={cart._id}>
          <ProductImage>
            <Image src={cart.imageUrl} />
          </ProductImage>

          <TextContent>
            <Column mobileView={mobileView} tabletView={tabletView} w40>
              <Name>{cart.name}</Name>
              <p>
                {" "}
                cung cấp bởi : <a href="#">Mai Van Thang</a>
              </p>
              <p>Chỉ còn 5 sản phẩm</p>
              <BtnActions>
                <ButtonLink>Xóa</ButtonLink>
                <ButtonLink>Thêm vào DS mua sau</ButtonLink>
              </BtnActions>
            </Column>
            <Column w30 style={{justifyContent : "flex-start"}}>
              <p style={{ color: "#dd2222" }}>Khuyến mãi :7%</p>
              <UnitPrice>22.990.000</UnitPrice>
              <ProductActionsChange>
                <Button>-</Button>
                <Quantity>2</Quantity>
                <Button>+</Button>
              </ProductActionsChange>
            </Column>
            <Column>
              <TotalPrice>49.000.000</TotalPrice>
            </Column>
          </TextContent>          
        </TableRow>
      ))}     
    </CartTableContainer>
  );
};
const mapStateToProps = createStructuredSelector({
  cartItems : selectCartItems
})

export default connect(mapStateToProps)(CartTable);
