import React from "react";
import { SaleOffContainer, SeleOffLabel } from "./sale-off.styles";
import { getSaleOffProducts } from "../../../utils/algorithms";
import Slider from "react-slick";
import ProductItem from "../../UI/product-item/product-item.component";
const saleOffProducts = getSaleOffProducts();
const SaleOff = ({ mobileView, tabletView }) => {
  const settings = {
    infinite: true,
    slidesToShow: mobileView || tabletView ? 1 : 3,
    slidesToScroll: 1,
    autoplay: mobileView || tabletView ? false : true,
    speed: mobileView || tabletView ? 500 : 2000,
    centerPadding: mobileView || tabletView ? "60px" : "auto",
    autoplaySpeed: 2000,
    cssEase: "linear",
    pauseOnHover: true,
    centerMode:mobileView || tabletView ?  true : false,
  };
  return (
    <SaleOffContainer>
      <SeleOffLabel>Mặt hàng đang giảm giá</SeleOffLabel>
      <Slider {...settings}>
        {saleOffProducts && saleOffProducts.length > 10
          ? saleOffProducts
              .filter((_, idx) => idx < 20)
              .map((product) => (
                <ProductItem key={product._id} product={product} />
              ))
          : null}
      </Slider>
    </SaleOffContainer>
  );
};

export default SaleOff;
