import React, {useState, useEffect} from 'react'
import {TaskbarContentWrapper} from "./task-content-item.styles";
const TaskbarContent = ({content, show}) => {
  useEffect(() => {
    console.log(parseInt(window.getComputedStyle(content).fontSize),16)
  }, [content]) 
  return (
    <TaskbarContentWrapper dangerouslySetInnerHTML={{__html : content}}>

    </TaskbarContentWrapper>
  )
}

export default TaskbarContent
