import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
  Editable,
} from "../../UI/custom-form-second/custom-form-second.component";
import { FormCreateProductWrapper } from "./form-create-product.styles";
import AppContext from "../../../context/app-viewport.context";
import { getListCategory, getListProductType } from "../../../utils/algorithms";
import ChipInput from "material-ui-chip-input";
import NumberFormat from 'react-number-format';
const FormCreateProduct = () => {
  const [listCatogory, setListCategory] = useState([]);
  const [listProductType, setListProductType] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",
  });
  const [selectedProductType, setSelectedProductType] = useState({
    _id: "",
    name: "",
    linkUrl: "",
  });
  const [name, setName] = useState("");
  const [tags, setTags] = useState([]);
  const [price, setPrice] = useState(0);  
  const [error, setError] = useState(null);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  useEffect(() => {
    let _mounted = true;
    getListCategory()
      .then((data) => {
        setListCategory(data);
      })
      .catch((err) => setError(err));
    return () => (_mounted = false);
  }, [getListCategory]);

  const handleChangeCategory = (e) => {
    const index = e.target.selectedIndex;
    const _id = e.target.value;
    const linkUrl = e.target.childNodes[index].dataset.url;
    const name = e.target[index].text;
    setSelectedCategory({ _id, name, linkUrl });
    getListProductType(_id)
      .then((data) => {
        setListProductType(data);
      })
      .catch((err) => setError(err));
  };
  console.log(tags)
  const handleChangeChip = chip => {
    setTags(chip)
  }
  const handleChangePrice = e => {
    console.log(e.target.value)
  }
  return (
    <FormCreateProductWrapper>
      <Form>
        <FormGroup>
          <Select
            defaultValue={selectedCategory._id}
            onChange={handleChangeCategory}
          >
            <Option value="" disabled>
              -- Nhóm sản phẩm --
            </Option>
            {listCatogory.map((category) => (
              <Option
                key={category._id}
                value={category._id}
                data-url={category.linkUrl}
              >
                {category.name}
              </Option>
            ))}
          </Select>
        </FormGroup>
        <FormGroup>
          <Select defaultValue={selectedProductType._id}>
            <Option value="" disabled>
              -- Loại sản phẩm --
            </Option>
            {listProductType.map((productType) => (
              <Option
                key={productType._id}
                value={productType._id}
                data-url={productType.linkUrl}
              >
                {productType.name}
              </Option>
            ))}
          </Select>
        </FormGroup>
        <FormGroup>
          <Label>Tên Sản phẩm</Label>
          <Input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label>Tạo tags</Label>
          <ChipInput
            placeholder="#abc"
            fullWidth
            defaultValue={tags}
            newChipKeyCodes={[32]}
            onChange={(chip) => handleChangeChip(chip)}
          />
        </FormGroup>
        <FormInline>
          <FormGroup>
            <Label>Giá sản phẩm</Label>
            <NumberFormat value={price} thousandSeparator={true} suffix={'Đ'} onChange={handleChangePrice}/>
          </FormGroup>
        </FormInline>
      </Form>
    </FormCreateProductWrapper>
  );
};

export default FormCreateProduct;
