  const responseFacebook = response => {
    console.log(response)
  }
  return (
    <FacebookLogin
      appId="1234252493592068"
      autoLoad
      fields="name,email,picture"
      callback={responseFacebook}
      icon={<TiSocialFacebookCircular/>}
      render={props => <CustomButton type="button" icon={<FaGooglePlusG/>} size="small"  color="white" bgColor="#EA4335" variant="contained" onClick={props.onClick} positionIcon="after">Sign In</CustomButton>   }
    />
  )
}

export default FBLogin
