import React, { useState, useEffect, useRef } from "react";
import { CategoryMenuContainer } from "./category-menu.styles";
import CATEGORY_DATA from "../../data/category";
import { CustomLink } from "../custom-link/custom-link.component";
import ProductsPopup from "../products-popup/products-popup.component";
const CategoryMenu = ({ show }) => {
  const [ctgId, setCtfId] = useState(null);
  const [offsetWidth, setOffsetWidth] = useState(0);
  const [isPopupHover, setIsPopUpHover] = useState(true);
  const [touched, setIsTouched] = useState(false)
  const categoryRef = useRef(null);
  const popUpRef = React.createRef(null);
  const categoriesList = Object.keys(CATEGORY_DATA).map(
    (key) => CATEGORY_DATA[key]
  );
  useEffect(() => {
    setOffsetWidth(categoryRef.current.offsetWidth);    
    console.log(touched, isPopupHover, ctgId)
  });

  const handleMouseOver = (e,categoryId) => {           
    e.preventDefault();
    setCtfId(categoryId)
  }
  const  handleMouseOut = (e) => {
    setCtfId(null)
  }
  return (
    <CategoryMenuContainer show={show} ref={categoryRef}>
      {categoriesList.map((item) => (
        <CustomLink 
          onMouseOut={(e) => handleMouseOut()}         
          onMouseOver={(e) => handleMouseOver(e,item._id)}
          key={item.name}
          to={item.linkUrl}
          style={{ fontWeight: "400", textTransform: "capitalize" }}
        >
          {item.name}
        </CustomLink>
      ))}
      {ctgId && (
        <ProductsPopup          
          ref={popUpRef}
          offsetWidth={offsetWidth}
          categoryId={categoryId}
        />
      )}
    </CategoryMenuContainer>
  );
};

export default CategoryMenu;
