import React, { useState } from "react";
import { FaLongArrowAltDown } from "react-icons/fa";
import {
  OrderedListPreviewContainer,
  Row,
  LeftMainContent,
  RightMainContent,
  MoreOrderedDetails,
  OrderedItemDetails,
} from "./ordered-list-preview.styles";
import OrderedItem from "../ordered-item/ordered-item.component";
const OrderedListPreview = ({ orderedItem }) => {
  console.log(orderedItem);
  const [show, setShow] = useState(false);

  return (
    <OrderedListPreviewContainer>
      <Row>
        <LeftMainContent>
          Thời gian đặt hàng :{" "}
          <strong>{new Date(orderedItem.createdAt).toLocaleString()}</strong>
        </LeftMainContent>
        <RightMainContent>
          <strong>{orderedItem.totalPrice.toLocaleString("es-AR")} VND</strong>
        </RightMainContent>
      </Row>
      <Row>
        <LeftMainContent>
          Tình trạng đơn hàng :{" "}
          <strong>
            {orderedItem.status === "active" ? (
              <span style={{ color: "#dd2222" }}>Đang vận chuyển</span>
            ) : (
              <span style={{ color: "green" }}>Hoàn tất</span>
            )}
          </strong>
        </LeftMainContent>
      </Row>
      <Row>
        <LeftMainContent>
          {orderedItem.time_complete ? (
            <span>Ngày giao hàng : <strong>{new Date(orderedItem.time_complete).toLocaleString()}</strong></span>
          ) : <span>Ngày giao hàng dự kiến : <strong>{new Date(orderedItem.time_expire).toLocaleString()}</strong></span> }
        </LeftMainContent>
      </Row>
      <Row>
        <LeftMainContent>
          Hình thức đặt hàng : <strong>{orderedItem.method_paymen === "cod" ? "Thanh toán bằng tiền mặt" : "Thanh toán qua thẻ"}</strong>
        </LeftMainContent>
      </Row>
      <MoreOrderedDetails onClick={() => setShow(!show)}>
        Ordered Items Detail{" "}
        <FaLongArrowAltDown style={{ fontWeight: "bold" }} />
      </MoreOrderedDetails>

      <OrderedItemDetails show={show}>
        {orderedItem.products.map((product, id) => (
          <OrderedItem
            key={product._id}
            item={product}
            timeout={250}
            show={show}
          />
        ))}
      </OrderedItemDetails>
    </OrderedListPreviewContainer>
  );
};

export default OrderedListPreview;
