import styled from "styled-components";

export const CategoryMenuContainer = styled.div`
  display : flex ; 
  flex-direction : column;
  padding : 0 1rem;
  height : ${({show}) => show ? "100%" : 0}; 
  overflow : auto;
  border : 1px solid #ccc;
  transition : height 0.3s ;
`
