import React from "react";
import { OrderItemContainer, ItemName, ItemPrice, ItemImage } from "./ordered-item.styles";
import { CSSTransition } from "react-transition-group";
import "./ordered-item.css";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
const OrderedItem = ({item, show, timeout }) => {
  console.log(item)
  console.log(show, timeout);
  const columns = [ { id: 'name', label: 'Name', minWidth: 170 },
  { id: 'image', label: '', width: 80 },
  {
    id: 'name',
    label: 'tên',
    width : "35%",
    align: 'right',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'size',
    label: 'Size\u00a0(km\u00b2)',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'density',
    label: 'Density',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toFixed(2),
  },]
  return (
    <CSSTransition in={show} timeout={timeout} unmountOnExit classNames="alert">    
      <OrderItemContainer>
        {/* <ItemImage src={`data:${item.image.mimetype};base64,${item.image.data}`}/>
        <ItemName>{item.name}</ItemName>
        <ItemPrice>${item.price * item.quantity}</ItemPrice> */}
        <TableContainer>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {/* {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })} */}
          </TableBody>
        </Table>
      </TableContainer>
      </OrderItemContainer>

    </CSSTransition>
  );
};

export default OrderedItem;
