import React , {useContext} from 'react'
import {SearchOverViewWrapper, Grid} from "./search-overview.styles";
import {selectProductList} from "../../../redux/search/search.selectors"
import {createStructuredSelector} from "reselect";
import {connect} from "react-redux";
import ProductItem from "../../UI/product-item/product-item.component"
import AppContext from "../../../context/app-viewport.context";
const SearchOverView = ({productList}) => {  
  return (
    <SearchOverViewWrapper>
      {productList.length ? (
        <Grid>
          {productList.map(product => (
            <ProductItem key={product._id} product={product}/>
          ))}
        </Grid>
      ) : <h4>Không tìm thấy sản phẩm nào</h4>}
    </SearchOverViewWrapper>
  )
}
const mapStateToProps = createStructuredSelector({
  productList : selectProductList
})
export default connect(mapStateToProps)(SearchOverView)
