import React, { useEffect, useState, useRef } from "react";
import {
  CommentReviewsItemWrapper,
  Image,
  Row,
  ReadMore,
  CommentText, 
  ButtonLink,
} from "./comment-reviews-item.styles";
import Moment from "react-moment";
import { FcBusinessman } from "react-icons/fc";
const CommentReviewsItem = ({ comment, product , setCommentItemHeight}) => {
  const commentItemReadMore = useRef(null)
  const [readMore, setReadMore] = useState(false);
  const [showReadMore, setShowReadMore] = useState(false);
  const [text, setText] = useState("");
  useEffect(() => {
    if(comment.text.length > 150){
      setText(comment.text.substr(0,150));
    }else{
      setText(comment.text)
    }
    // if(commentItemReadMore.current.scrollHeight > commentItemReadMore.current.clientHeight ){  
    //   console.log(commentItemReadMore.current.scrollHeight)    
    //   setShowReadMore(true);         
    //   setCommentItemHeight(commentItemReadMore.current.scrollHeight)
    //  }
  }, [comment])  
  return (
    <CommentReviewsItemWrapper ref={commentItemReadMore}>
      <Row>
        <Image
          src={
            comment.user.avatar === "avatar-default.png"
              ? `http://localhost:5000/images/${comment.user.avatar}`
              : null
          }
        />
        <Row style={{flexDirection:"column"}}>
          <CommentText>
            <p>
              <strong>
                {comment.user.name}{" "}
                {comment.user._id === product.user._id ? (
                  <span style={{ color: "#3f51b5" }}>
                    Nhà bán hàng <FcBusinessman />
                  </span>
                ) : (
                  ""
                )}
              </strong>{" "}
              <span style={{ color: "rgba(0,0,0,0.75)" }}>
                <Moment format="DD-MM-YYYY HH:mm">{comment.createdAt}</Moment>
              </span>{" "}
            </p>
            {comment.text}
            {showReadMore  ? <ReadMore onClick={() => setReadMore(true)}>{!readMore ? "Xem thêm" : "Thu gọn"}</ReadMore> : null} 
          </CommentText>       
          <div>
            <ButtonLink>Thích ({comment.likes.length})</ButtonLink>
            <ButtonLink>Không thích ({comment.dislikes.length})</ButtonLink>
            <ButtonLink>Chia sẻ</ButtonLink>
          </div>
        </Row>
      </Row>
    </CommentReviewsItemWrapper>
  );
};

export default CommentReviewsItem;
