import searchActionTypes from "./search.types";
import axios from "axios";
import urls from "../../utils/urls";

export const setSearchKey = (searchKey) => ({
  type: searchActionTypes.SET_SEARCH_KEY,
  payload: searchKey,
});

export const searchStart = () => ({
  type: searchActionTypes.SEARCH_START,
});
export const searchSuccess = (listProducts) => ({
  type: searchActionTypes.SEARCH_SUCCESS,
  payload: listProducts,
});
export const searchFail = (error) => ({
  type: searchActionTypes.SEARCH_FAIL,
  payload: { msg: error.response.data.message, status: error.response.status },
});

export const onSearch = (searchKey) => async (dispatch) => {
  try {
    dispatch(searchStart());
    const { data } = await axios.get(urls.SEARCH_PRODUCT_LIST, {
      params: { searchKey },
    });
    dispatch(searchSuccess(data));
  } catch (error) {
    dispatch(searchFail());
  }
};
