import styled , {keyframes} from "styled-components";

export const LoaderWrapper = styled.div`
  display : fixed ;
  left: 0 ;
  right : 0 ;
  top : 0; 
  bottom : 0;
  background-color : black; 
  animation : ${loaderWrapper} 0.5s ;
  z-index : 999
`

export const loaderWrapper = keyframes`
  0%{
    opacity : 1; 
  }
  50%{
    opacity : 0 ; 
  }
  100%{
    opacity : 1 ;
  }
`
