import React, { useEffect, useState } from "react";
import { ProductsPopupContainer } from "./products-popup.styles";
import { selectMenuList } from "../../../redux/menu/menu.selectors"
import ProductsPopupItem from "../products-popup-item/products-popup-item.component";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
const ProductsPopup = ({
  offsetWidth,
  categoryId,
  productTypeList,
  productList,
}) => {
  const [productTypesByCategoryId, setProductTypesByCategoryId] = useState(
    null
  );
  useEffect(() => {
    setProductTypesByCategoryId(
      productTypeList.filter(
        (productType) =>
          productType.category.toString() === categoryId.toString()
      )
    );
  }, [categoryId]);
  console.log(productTypesByCategoryId);
  return (
    <React.Fragment>
      {categoryId && productTypesByCategoryId ? <ProductsPopupContainer offsetWidth={offsetWidth}>
       {productTypesByCategoryId && productTypesByCategoryId.map((productType) => (
          <ProductsPopupItem
            key={productType._id}
            productLabel={productType.name}
            productTypeId={productType._id}
            productList={productList}
          />
        ))} 
    </ProductsPopupContainer> : null}
    </React.Fragment>

  );
};
const mapStateToProps = createStructuredSelector({
 menuList : selectMenuList
});
export default connect(mapStateToProps)(ProductsPopup);
