import CATEGORY_DATA from "../data/category";

let categoryList = CATEGORY_DATA;

export const flattenListSellers = () => {
  console.log(categoryList);
  let listItems = [];
  Object.keys(categoryList).forEach((category) => {
    Object.keys(categoryList[category].items).forEach((item) => {
      categoryList[category].items[item].forEach((item) => {
        item.sellers.forEach((seller) => {
          listItems = listItems.concat({ ...seller, name: item.name });
        });
      });
    });
  });
  return listItems;
};

export const getTop10LatestProducts = (categoryList) => {
  let products = flattenListSellers(categoryList);
  products.sort((a, b) => {
    if (Date.parse(a.createdAt) < Date.parse(b.createdAt)) return 1;
    else return -1;
  });
  return products.slice(0, 10);
};
