import React, {useState, useEffect} from "react";
import {
  ProductsPopupItemContainer,
  Label,
  CustomLink,
} from "./products-popup-item.styles";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
import {selectProductList} from "../../../redux/product/product.selectors"
const ProductsPopupItem = ({ productLabel, productList, productTypeId }) => {
  const [products, setProducts] = useState(null);
  useEffect(() => {
    if(productTypeId && productList && productList.length){
      console.log(productTypeId)
      setProducts(productList.filter(item => item.productType.toString() === productTypeId.toString()));
    }
  }, [productTypeId])  
  return (

    <ProductsPopupItemContainer>
      {/* <Label>{productLabel}</Label>
      {productList && productList.length && (
        productList.map((product) => (
          <CustomLink title={product.name} to={product.linkUrl}>
            {product.name}
          </CustomLink>
        ))
      )} */}
    </ProductsPopupItemContainer>
  );
};
const  mapStateToProps = createStructuredSelector({  
  productList : selectProductList
})
export default connect(mapStateToProps)(ProductsPopupItem);
