import categoryActionTypes from "./category.types";

export const fetchCategoryStart = (path) => ({
  type: categoryActionTypes.FETCH_CATEGORY_LIST_START,
  payload: path,
});
export const fetchCategorySuccess = ({
  productTypeList,
  discountProductList,
  topRatedProducts,
  BestSellerProducts,
  ProductsPerPage,
  numProducts,
  numPages,
}) => ({
  type: categoryActionTypes.FETCH_CATEGORY_LIST_SUCCESS,
  payload: {
    productTypeList,
    discountProductList,
    topRatedProducts,
    BestSellerProducts,
    ProductsPerPage,
    numProducts,
    numPages,
  },
});
export const fetchCategoryFail = (err) => ({
  type: categoryActionTypes.FETCH_CATEGORY_LIST_FAIL,
  error: err,
});
