import React from 'react'
import {SaleOffContainer, SeleOffLabel} from "./sale-off.styles";
import {getSaleOffProducts} from "../../../utils/algorithms"
import Slider from "react-slick";

const saleOffProducts = getSaleOffProducts();
const SaleOff = () => { 
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear"
  };
  return (
    <SaleOffContainer>
      <SeleOffLabel>Sản phẩm giảm giá</SeleOffLabel>
      <Slider {...settings}>
        {saleOffProducts && saleOffProducts.length < 10 ? 
          saleOffProducts.map(product => (
            <div>{product.name}</div> 
          ))  
          :
          <div>{product.name}</div>
      }
      </Slider>
    </SaleOffContainer>
  )
}

export default SaleOff
