import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
const Pagination = ({currentPage, numPages}) => {
  let listPages = [] ; 
  if(currentPage===1){
    if(numPages >= 4){
      listPages = [1,2,3,4];
    }else{
      for(let i = 0 ; i < numPages; i++ ){
        listPages.push(i+1);
      }
    }
  }

  return (
    <PaginationContainer>
       <PageLink>Về trước</PageLink>
      {listPages.length && listPages.map(page => (
        <PageLink to="/?page=1">
        1
      </PageLink>
      ))



    }
      <PageLink to="/?page=5">Tiếp</PageLink>
    </PaginationContainer>
  );
};

export default Pagination;
