import styled from "styled-components";

export const TaskbarContentWrapper = styled.div`
  width : 100%;   
  height: 100%; 
`

export const EmbberContent = styled.div`
  width: 100%; 
  height : 90%;
  overflow : hidden ;
  text-overflow : ellipsis ; 
  white-space: nowrap;  
  word-break: break-all; 
`

export const ReadMore = styled.span`
  text-align:center; 
  cursor : pointer;
  position : absolute; 
  top : 1005;
`
