import {createGlobalStyle} from "styled-components"

export const GlobalStyle = createGlobalStyle`
  body{
    @media screen and(min-width : 992px){      
      font-size : 16px;
    }
    @media screen and(max-width: 992px){      
        font-size : 14px;

    }
  }

`
