import React, {useEffect, useState} from 'react'
import {getCommentReviewsByProductId} from "../../../utils/connectDB";
import Spinner from "../../UI/spinner/spinner.component"
import {CommentReviewsWrapper} from "./comment-reviews.styles"
const CommentReviews = ({show,productId}) => {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  useEffect(() => {
    let _mounted = true ; 
    setLoading(true);
    getCommentReviewsByProductId().then(data => {
      if(_mounted){
        setComments(data);
        setLoading(false);
      }      
    })
    .catch(err => {
     if(_mounted){
      setError(err);
      setLoading(false);
     }
    })
    return () => {_mounted = false}
  }, [productId])
  return (
    <CommentReviewsWrapper>

    </CommentReviewsWrapper>
  )
}

export default CommentReviews
