import styled from "styled-components";

export const MasterHeaderContainer = styled.div`
  display : flex ;   
  height : 4rem;
  width : 100%;
  position : relative;
  padding : 0 1.5rem;
`

export const Grid = styled.div`
  flex : 1 ;
  width : ${({w50}) => w50 ? "50%" : "auto"}
`
