import React, { useEffect, useState } from "react";
import ReactCountryFlag from "react-country-flag";
import {ToggleLanguageContainer, Text, LanguageList} from "./toggle-language.styles";
const configLang = {
  us: { text: "USA", code: "US" },
  vi: { text: "Tiếng Việt", code: "VN" },
};

const ToggleLanguage = ({ lang }) => {
  const [defaultLang, setDefaultLang] = useState(configLang.vi);
  const listCountriesCode = Object.keys(configLang).filter(keys => configLang[keys].code !== defaultLang.code);
  return (
    <ToggleLanguageContainer>     
      <ReactCountryFlag
        countryCode={defaultLang.code}
        svg
        style={{ transform: "scale(1.5)", marginRight: "1rem" }}
      />      
      <Text>{defaultLang.text}</Text>
      <LanguageList>
        {listCountriesCode.map(code => (
          <React.Fragment>
            <ReactCountryFlag
            countryCode={defaultLang.code}
            svg
            style={{ transform: "scale(1.5)", marginRight: "1rem" }}
        />      
          <Text>{defaultLang.text}</Text>
          </React.Fragment>
        ))}
      </LanguageList>
    </ToggleLanguageContainer>
  );
};

export default ToggleLanguage;
