import React, { useState, useEffect } from "react";
import { PaginationContainer, PageLink } from "./pagination.styles";

import { connect } from "react-redux";
import Paginate from "../../UI/paginate/paginate.component";
const Pagination = ({
  currentPage,
  numPages,  
  handlePageClick
}) => {  


  return (
    <PaginationContainer>     
      <Paginate numPages={numPages} handlePageClick={handlePageClick} currentPage={currentPage}/>
    </PaginationContainer>
  );
};

const mapDispatchToProps = dispatch => ({
  setCurrentPage : page => dispatch(setCurrentPage(page))
})
export default connect(null, mapDispatchToProps)(withRouter(Pagination));
