import React, { useEffect, useState } from "react";
import {
  CommentReviewsItemWrapper,
  Image,
  Row,
  CommentText,
  ButtonGroups,
  ButtonLink,
} from "./comment-reviews-item.styles";
import Moment from "react-moment";
import { FcBusinessman } from "react-icons/fc";
const CommentReviewsItem = ({ comment, product }) => {
  const [readMore, setReadMore] = useState(false);
  console.log(product);
  return (
    <CommentReviewsItemWrapper>
      <Row>
        <Image
          src={
            comment.user.avatar === "avatar-default.png"
              ? `http://localhost:5000/images/${comment.user.avatar}`
              : null
          }
        />
        <Row>
          <CommentText>
            <p>
              <strong>
                {comment.user.name}{" "}
                {comment.user._id === product.user._id ? (
                  <span style={{ color: "#3f51b5" }}>
                    Nhà bán hàng <FcBusinessman />
                  </span>
                ) : (
                  ""
                )}
              </strong>{" "}
              <span style={{ color: "rgba(0,0,0,0.75)" }}>
                <Moment format="DD-MM-YYYY HH:mm">{comment.createdAt}</Moment>
              </span>{" "}
            </p>
            {comment.text}
          </CommentText>       
          <div>
            <ButtonLink>Thích ({comment.likes.length})</ButtonLink>
            <ButtonLink>Không thích</ButtonLink>
            <ButtonLink>Chia sẻ</ButtonLink>
          </div>
        </Row>
      </Row>
    </CommentReviewsItemWrapper>
  );
};

export default CommentReviewsItem;
