  const [readMore, setReadMore] = useState(false);  
  console.log("OK")
  return (
    <CommentReviewsItemWrapper>      
      <Row>      
        <Image src={comment.user.avatar === "avatar-default.png" ? `http://localhost:5000/images/${comment.user.avatar}` : null}/>    
        <CommentText>
          <Row><strong>{comment.user.name}</strong> - {comment.createdAt}</Row>
        {comment.text}
        </CommentText>


      </Row>
    </CommentReviewsItemWrapper>
  )
}

export default CommentReviewsItem
