import styled from "styled-components";

export const  NavigationWidgetWrapper = styled.div`
  width : 100%;
  height : 100%;
  display : flex ; 
  justify-content : center;
  border: 1px solid ;
  align-items:center; 

`
export const Option = styled.span`
  padding : 0 1rem;
  position :relative;
`


export const CartPrice = styled.span`
  font-weight : bold ;  
  font-size : .9em;
  over-flow : hidden;
  white-space: nowrap; 
  text-overflow : ellipsis;
`
