import styled from "styled-components";
export const BannerOverViewContainer = styled.section`
  display : block ;   
  height : 500px;
  width : ${({smallView}) => smallView ? "100%" : "75%"};
  margin : ${({smallView}) => smallView ? "auto" : "0 0 0 auto"};
  padding : 1rem; 

`

export const Grid = styled.div`
  width : ${({w25}) => w25 ? "25%" : "75%"}
`
