import React, {useEffect, useRef} from "react";
import {NavigationWidgetWrapper, Option, CartPrice, CartNumber} from "./navigation-widget.styles";
import Icon from "../../UI/custom-icon/custom-icon.component";
import {FaHeart, FaShoppingBag} from "react-icons/fa"
import {AiOutlineShoppingCart} from "react-icons/ai";
import {createStructuredSelector} from "reselect";
import {selectCountItem} from "../../../redux/cart/cart.selectors";
import {connect} from "react-redux"
import {setCartCheckoutPosition} from "../../../redux/cart/cart.actions";
const NavigationWidget = ({countItem, setCartCheckoutPosition}) => {
  const cartRef = useRef(null)
  useEffect(() => {
    if(cartRef.current){
      setCartCheckoutPosition(cartRef.current.offsetTop)
    }
  },[cartRef.current])
  return (
    <NavigationWidgetWrapper>
      <Option>
        <Icon icon={<FaHeart />} number={1} />
      </Option>
      <Option>
        <Icon icon={<FaShoppingBag />} number={5} />
      </Option>
      <Option style={{flexGrow : 1, textAlign : "right"}}>
        <CartPrice ref={cartRef}><AiOutlineShoppingCart style={{fontSize:"1.1em",verticalAlign:"text-top"}}/> Giỏ hàng : <CartNumber>{countItem}</CartNumber></CartPrice>
      </Option>
    </NavigationWidgetWrapper>
  );
};
const mapStateToProps = createStructuredSelector({
  countItem : selectCountItem
})
const mapDispatchToProps = dispatch => ({
  setCartCheckoutPosition : position => dispatch(setCartCheckoutPosition(position))
})
export default connect(mapStateToProps, mapDispatchToProps)(NavigationWidget);
