import React , {useState} from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
const Pagination = ({currentPage, numPages}) => {
  const [listPages, setListPages] = useState([]);
  if(currentPage === 1 ){
    if(numPages > 4 ){
      setListPages([1,2,3,4])
    }else{
      setListPages(Array(numPages).map((_,idx) => idx + 1 ));
    }
  }
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      <PageLink to="/?page=1">
        1
      </PageLink>
      <PageLink to="/?page=2">2</PageLink>
      <PageLink to="/?page=3">3</PageLink>
      <PageLink to="/?page=4">4</PageLink>
      <PageLink to="/?page=5">Tiếp</PageLink>
    </PaginationContainer>
  );
};

export default Pagination;
