import styled from "styled-components";

export const Grid = styled.div`
  width : ${({w50}) => w50 ? "50%" : "25%"};
`

export const MasterHeaderContainer = styled.div`
  display : flex ;
  flex-direction : ${({isMobile}) => isMobile ? "column" : "row"}   ;
  height : 3.5rem;
  width : 100%;
  position : relative;
  padding : 0 1.5rem;
`
