import styled from "styled-components";

export const HeroContainer = styled.div`
  width : 80%; 
  height : 500px;
  margin: auto;
  display : grid ;   
  grid-template-columns : 1fr 3fr;
  grid-gap : .3rem;
`

export const LeftSide = styled.div`   
  height : 100%;
`

export const RightSide = styled.div`  
  height : 100%;
`
