import styled from "styled-components";

export const Option = styled.span`
  padding : 0 1rem;
  position :relative;
  &:not(:last-child){
    margin-bottom : 1rem;
  }
`

export const RowInline = styled.div`
  width : 100%;
  display : flex ;   
  & > *{
    display : flex ; 
  }  
`
