import React, { useState, useContext, useEffect, useRef } from "react";
import {
  MasterSearchContainer,
  CategoryTypes,
  SearchInput,
  SearchButton,
} from "./master-search.styles";
import { FaSearch } from "react-icons/fa";
import {onSearch} from "../../../redux/search/search.actions"
import AppContext from "../../../context/app-viewport.context";
import {withRouter} from "react-router-dom"
import {connect} from "react-redux";
const MasterSearch = ({history, onSearch}) => {
  const [textSearch, setTextSearch] = useState("");
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const width = useContext(AppContext);  
  const searchRef = useRef(null);  
  useEffect(() => {
    if (width < 600) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
  }, [width]);

  const handleSearch = (e) => {
    e.preventDefault();
    history.push(`/search?key=${textSearch}`)
  }
  return (
    <MasterSearchContainer mobileView={mobileView} onSubmit={handleSearch} ref={searchRef}>      
      <SearchInput
        type="text" placeholder="Tìm kiếm" 
        value={textSearch}
        onChange={(e) => setTextSearch(e.target.value)}
        style={{          
          fontSize: mobileView ? "1em" : "1.2em",          
          padding : mobileView ? "0 0.2rem" : "0 1rem",
          width :   mobileView ? "70%" : "50%"
        }}
      />
      <SearchButton mobileView={mobileView}><FaSearch/></SearchButton>
    </MasterSearchContainer>
  );
};
const mapDispatchToProps = dispatch => ({
  onSearch : (searchKey) => dispatch(onSearch(searchKey))
})
export default connect(null, mapDispatchToProps)(withRouter(MasterSearch));
