import styled from "styled-components";

export const  NavigationWidgetWrapper = styled.div`
  width : 100%;
  height : 100%;
  display : flex ; 
  justify-content : center;
  align-items:center; 

`
export const Option = styled.span`
  padding : 0 1rem;
  position :relative;
`


export const CartPrice = styled.span`  
  font-size : 1em;
  over-flow : hidden;
  white-space: nowrap; 
  text-overflow : ellipsis;
  border: 1.5px solid #ccc;
  padding : 0.5rem 1rem;
  border-radius : 7px;
`

export const CartNumber = styled.span`
  padding: 3px; 
  background-color : #fbc531; 
  color : white;
  border-radius : 5px; 
`
