import React from "react";
import { OrderItemContainer, ItemName, ItemPrice, ItemImage } from "./ordered-item.styles";
import { CSSTransition } from "react-transition-group";
import "./ordered-item.css";
import {Table, Thead,Row, Th, Td, Tbody} from "../../UI/custom-table/custom-table.styles";
const OrderedItem = ({item, show, timeout }) => {
  console.log(item)
  console.log(show, timeout);
  return (
    <CSSTransition in={show} timeout={timeout} unmountOnExit classNames="alert">    
    <Table>
      <Thead>
        <Row>
          <Th width={15}></Th>
          <Th width={30}>Tên SP</Th>
          <Th width={10}>Số lượng</Th>
          <Th width={15}>Đơn giá</Th>
          <Th width={10}>Khuyến mãi</Th>
          <Th width={20}>Thành tiền</Th>
        </Row>
      </Thead>
      <Tbody>
        <Row>
          <Td width={15}>
            <ItemImage src={`data:${item.image.mimetype};base64,${item.image.data}`}/>
          </Td>
          <Td width={30}>{item.name}</Td>
          <Td width={10}>{item.quantity}</Td>
          <Td width={15}>{item.price}</Td>
          <Td width={10}>{item.discount}</Td>
          <Td width={20}>{item.price*item.quantity*(100-item.discount) / 100}</Td>
        </Row>
      </Tbody>
    </Table>
{/* 
      <OrderItemContainer>
        <ItemImage src={`data:${item.image.mimetype};base64,${item.image.data}`}/>
        <ItemName>{item.name}</ItemName>
        <ItemPrice>${item.price * item.quantity}</ItemPrice>

      </OrderItemContainer> */}

    </CSSTransition>
  );
};

export default OrderedItem;
