import React from "react";
import {
  CheckoutFormWrapper,
  Title,
  Button,
  ButtonLink,
} from "./check-out-form.styles";
import { withRouter } from "react-router-dom";
const CheckoutForm = ({ currentUser, show, history, match, setToggleEditUserForm }) => {
  if (
    !currentUser ||
    !currentUser.information.first_name ||
    !currentUser.information.last_name ||
    !currentUser.information.address ||
    !currentUser.information.ward ||
    !currentUser.information.city ||
    !currentUser.information.district ||
    !currentUser.information.email ||
    !currentUser.information.phone
  ) {
    return setToggleEditUserForm()
  }
  return (
    <CheckoutFormWrapper show={show}>
      <Title>Thông tin khách hàng</Title>
      <p>
        Tên khách hàng :{" "}
        <span
          style={{ fontWeight: "bold" }}
        >{`${currentUser.information.first_name} ${currentUser.information.last_name}`}</span>
      </p>
      <p>
        Địa chỉ :{" "}
        {`${currentUser.information.address}, phường ${currentUser.information.ward}, Quận ${currentUser.information.district}, ${currentUser.information.city}`}
      </p>
      <p>Email : {currentUser.information.email}</p>
      <p>Điện thoại : {currentUser.information.phone}</p>
      <ButtonLink
        onClick={setToggleEditUserForm}
      >
        Cập nhật thông tin
      </ButtonLink>
    </CheckoutFormWrapper>
  );
};

export default CheckoutForm;
