import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
  Editable,
} from "../../UI/custom-form-second/custom-form-second.component";
import {FormCreateProductWrapper} from "./form-create-product.styles"
import AppContext from "../../../context/app-viewport.context";
import {
  getCategoryList
} from "../../../utils/algorithms";

const FormCreateProduct = () => {
  const [listCatogory, setListCategory]  = useState([]);
  const [category, setCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",    
  });
  const [productTypeUrl, setProductTypeUrl] = useState(null);
  const [listProducts, setListProducts] = useState([]);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  useEffect(()=>{

  },[])  


  return (    
    <FormCreateProductWrapper >
      <Form>
        <FormGroup>
          <Select defaultValue={category._id}>
            <Option value="" disabled>-- Nhóm sản phẩm --</Option>
            {listCatogory.map(category => (
              <Option key={category._id} value={category._id}>{category.name}</Option>
            ))}
          </Select>
        </FormGroup>
      </Form>
    </FormCreateProductWrapper>
  );
};

export default FormCreateProduct;
