import styled from "styled-components";

export const ProductsPopupContainer = styled.div`
  position : fixed;  
  left : ${({offsetWidth}) => `calc(${offsetWidth}px)` };
  top : 4rem;
  width : 60vw;
  height : 80vh;
  background-color : white; 
  border : 1px solid #ccc;
  z-index: 500;
`
