import React, { useEffect, useState } from "react";
import ReactCountryFlag from "react-country-flag";
import {ToggleLanguageContainer, Text} from "./toggle-language.styles";
const configLang = {
  us: { text: "USA", code: "US" },
  vi: { text: "Tiếng Việt", code: "VN" },
};

const ToggleLanguage = ({ lang }) => {
  const [defaultLang, setDefaultLang] = useState(configLang.vi);

  return (
    <ToggleLanguageContainer>     
      <ReactCountryFlag
        countryCode={defaultLang.code}
        svg
        style={{ transform: "scale(1.5)", marginRight: "1rem" }}
      />      
      <Text>{defaultLang.text}</Text>
      <LanguageList>
        {Object.keys(configLang).filter(keys => configLang[keys].code !== defaultLang.code).map(lang => (
          <Text key={lang.code} value={lang.code} name={lang.text} >{lang.text}</Text>
        ))}
      </LanguageList>
    </ToggleLanguageContainer>
  );
};

export default ToggleLanguage;
