import styled from "styled-components";


export const MasterHeaderContainer = styled.div`
  display : flex ;
  flex-direction : ${({isMobile}) => isMobile ? "column" : "row"}   ;
  height : 3.5rem;
  width : 100%;
  position : relative;
  padding : 0 1.5rem;

`


export const Grid = styled.div`
  width : ${(props) => props.isMobile ? "100%" : props.w50 ? "50%" : "25%"};
`
